import React, { useState } from "react";
import style from './AddUser.module.css'
import Card from "../UI/Card";
import Button from "../UI/Button";
const AddUser = () => {
    const [eneterdUserName, setEnterdUserName] = useState('')
    const [eneterdUserAge, setEnteredUserAge] = useState('')
    const addUserHandler = (e) => {
        e.preventDefault();
        console.log(eneterdUserName, eneterdUserAge)
        const addUserValues = {
            name: eneterdUserName,
            age: eneterdUserAge,
        }
        setEnterdUserName('')
        setEnteredUserAge('')
        console.log(addUserValues)
    }
    const userNameChangeHandler = (e) => {
        setEnterdUserName(e.target.value)
    }
    const userAgeChangeHandler = (e) => {
        setEnteredUserAge(e.target.value)
    }
    return (
        <>
            <Card className={style.input}>
                <form onSubmit={addUserHandler}>
                    <label htmlFor="username">User Input</label>
                    <input id="username" type="text"
                        value={enterUsername}
                        onChange={userNameChangeHandler} />

                    <label htmlFor="age">Age</label>
                    <input id="age" type="number" min='1'
                        onChange={userAgeChangeHandler} />

                    <Button type="submit" >Add User</Button>
                </form>
            </Card>

        </>
    );
}

export default AddUser;
